import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const LoginPage = () => {
  const [loginDetails, setLoginDetails] = useState({
    username: '', 
    password: ''
  });
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setLoginDetails(prevDetails => ({
      ...prevDetails,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('https://express-t4.onrender.com/api/login', loginDetails, {
        headers: {
          'Content-Type': 'application/json'
        }
      });
      if (response.data.message === "Login success") {
        // Navigate to '/profile-listing' upon successful login
        navigate('/profile-listing');
      } else {
        // Handle any other messages or errors (if the API response format allows it)
        setErrorMessage(response.data.message || 'Login failed. Please check your credentials and try again.');
      }
    } catch (error) {
      // Detailed error handling
      if (error.response) {
        // The server responded with a status code out of the 2xx range
        setErrorMessage(error.response.data.message || 'An error occurred. Please try again later.');
      } else if (error.request) {
        // The request was made but no response was received
        setErrorMessage('The request was made but no response was received. Please check your network connection.');
      } else {
        // Something else caused the request to fail
        setErrorMessage('An error occurred. Please try again later.');
      }
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="username">Username:</label>
          <input
            type="text" // Changed to 'text' as we are using username
            name="username"
            value={loginDetails.username}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            name="password"
            value={loginDetails.password}
            onChange={handleChange}
            required
          />
        </div>
        {errorMessage && <div style={{ color: 'red' }}>{errorMessage}</div>}
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default LoginPage;
