// ProfileDetailsPage.jsx
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const ProfileDetailsPage = () => {
  const [userDetails, setUserDetails] = useState(null);
  const { id } = useParams();

  useEffect(() => {
    const fetchUserDetails = async () => {
      const response = await axios.get(`https://express-t4.onrender.com/api/users/${id}`);
      setUserDetails(response.data);
    };
    fetchUserDetails();
  }, [id]);

  if (!userDetails) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h2>{userDetails.name}</h2>
      <img src={userDetails.picture} alt={userDetails.name} />
      <div>Email: {userDetails.email}</div>
      <div>Company: {userDetails.company}</div>
      <div>About: {userDetails.about}</div>
      {/* Display other details as needed */}
    </div>
  );
};

export default ProfileDetailsPage;
