import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const ProfileListingPage = () => {
  const [users, setUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await axios.get('https://express-t4.onrender.com/api/users');
        setUsers(response.data);
      } catch (error) {
        console.error("Failed to fetch users:", error);
        // Handle error appropriately
      }
    };
    fetchUsers();
  }, []);

  const filteredUsers = searchTerm
    ? users.filter(user =>
        user.name.toLowerCase().includes(searchTerm.toLowerCase()))
    : users;

  return (
    <div>
      <h2>Users</h2>
      <input
        type="text"
        placeholder="Search by name..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        style={{ margin: '20px 0', padding: '10px' }}
      />
      <div style={{ display: 'flex', flexWrap: 'wrap' }}>
        {filteredUsers.map(user => (
          <div key={user._id} style={{ margin: '10px', cursor: 'pointer' }} onClick={() => navigate(`/profile-details/${user._id}`)}>
            <img src={user.picture} alt={user.name} style={{ width: '100px', height: '100px' }} />
            <div>{user.name}</div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProfileListingPage;
